import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'
import './moviedetails.css'

export const MovieDetails = () => {
  const { movieId } = useParams()
  const [movies, setMovies] = useState([])

  useEffect(() => {
    fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=1a0b43b3e6aab8215b964c141c11111b&language=en-US`)
      .then((res) => res.json())
      .then((json) => {
        setMovies(json)
      })
  }, [movieId])

  return (
    <section className="info-container" style={{ backgroundImage: `url(https://image.tmdb.org/t/p/w1280${movies.backdrop_path})` }}>
      <Link className="back-link" to={'/'}>
        <p>Movies</p>
      </Link>


      <div className="movie-info">
        <h1>{movies.title} <span className="voting">{movies.vote_average}/10</span></h1>
        <p>{movies.overview}</p>
      </div>


    </section>
  )
}
